INSERT INTO `creature_text` VALUES (24534, 0, 0, 'Welcome to Fort Wildervar, brave homesteaders! There\'s a whole continent out there just waiting to be claimed!', 12, 7, 100, 1, 0, 0, 'Marrod Silvertongue');
INSERT INTO `creature_text` VALUES (24534, 1, 0, 'True, Northrend is a hard land, but our people are strong, hardy, and equal to the task!', 12, 7, 100, 0, 0, 0, 'Marrod Silvertongue');
INSERT INTO `creature_text` VALUES (24534, 2, 0, 'We will win this land with the sword, and break it with the plow! You are the men and women who will be remembered for taming the wild continent!', 12, 7, 100, 1, 0, 0, 'Marrod Silvertongue');
INSERT INTO `creature_text` VALUES (24534, 3, 0, 'But, you will not be alone out there. My men and I have prepared pack mules carrying the supplies you\'ll need most.', 12, 7, 100, 1, 0, 0, 'Marrod Silvertongue');
INSERT INTO `creature_text` VALUES (24534, 4, 0, 'Axes, picks, seed, nails, food, blankets, water... it\'s all there, waiting for you. I think you\'ll find my prices quite reasonable, too.', 12, 7, 100, 25, 0, 0, 'Marrod Silvertongue');
INSERT INTO `creature_text` VALUES (24534, 5, 0, 'There are more than enough to go around. Should you need other goods, don\'t hesitate to ask!', 12, 7, 100, 1, 0, 0, 'Marrod Silvertongue');
INSERT INTO `creature_text` VALUES (24534, 6, 0, 'Now, my loyal custo... err, friends, go forth and conquer this land for our people!', 12, 7, 100, 274, 0, 0, 'Marrod Silvertongue');
