INSERT INTO `creature_text` VALUES (27033, 0, 0, 'Get out of there.', 12, 0, 100, 1, 0, 0, 'Text 1');
INSERT INTO `creature_text` VALUES (27033, 1, 0, 'If you want to jump to your death this time, I won\'t stop you.', 12, 0, 100, 1, 0, 0, 'Text 2');
INSERT INTO `creature_text` VALUES (27156, 0, 0, 'The mine is lost. All of our defenders have perished and the miners resurrected as Scourge aberrations!', 12, 0, 100, 1, 0, 0, 'Text 1');
INSERT INTO `creature_text` VALUES (27156, 1, 0, 'Grim news, my pint--sized companion. Should we suffer a defeat here the ramifications will be felt across this frozen wasteland. Lord Fordragon and our armies at the Wrathgate will be defenseless against an assault from Naxxramas.', 12, 0, 100, 1, 0, 0, 'Text 2');
INSERT INTO `creature_text` VALUES (27156, 2, 0, 'Yes, yes, I haven\'t forgotten Hyjal, or Silithus, or the march of the Legion, or every other damnable confrontation the 7th Legion has faced. It... I don\'t want the soldiers to hear this, Two--bit, but for the first time in my life...', 12, 0, 100, 1, 0, 0, 'Text 3');
INSERT INTO `creature_text` VALUES (27156, 3, 0, 'You are right, of course, old friend. Let us hope that these heroes arrive soon.', 12, 0, 100, 1, 0, 0, 'Text 4');
INSERT INTO `creature_text` VALUES (27159, 0, 0, 'It does get worse, old friend. According to my calculations, without suitable materials to repair our parapets, the walls protecting this keep will crumble in twelve hours.', 12, 0, 100, 1, 0, 0, 'Text 1');
INSERT INTO `creature_text` VALUES (27159, 1, 0, 'We have been through worse and survived, Leoric. Do not forget Hyjal...', 12, 0, 100, 1, 0, 0, 'Text 2');
INSERT INTO `creature_text` VALUES (27159, 2, 0, 'Say no more, Leoric. We all feel the chill in our bones. Yet, each day, heroes arrive from across the world -- and beyond.', 12, 0, 100, 1, 0, 0, 'Text 3');
INSERT INTO `creature_text` VALUES (27159, 3, 0, 'I have heard whisperings from the east that our esteemed Scarab Lord has joined the battle, as have entire batallions from Shattrath: heroes blessed by A\'dal himself, granted rank as Hand of A\'dal!', 12, 0, 100, 1, 0, 0, 'Text 4');
INSERT INTO `creature_text` VALUES (27160, 0, 0, '%s attempts to run away in fear!', 16, 0, 100, 0, 0, 0, 'combat Flee');
