INSERT INTO `creature_text` VALUES (27191, 0, 0, 'I\'m gonna jump this time Brote, I swear it!', 12, 0, 100, 1, 0, 0, 'Text 1');
INSERT INTO `creature_text` VALUES (27198, 0, 0, 'If I get out of here, I will CRUSH every clock I come into contact with...', 12, 0, 100, 1, 0, 0, 'Text 1');
INSERT INTO `creature_text` VALUES (27198, 1, 0, 'And you know what pisses me off more than anything, Skrotee? No, not that we\'ve been made into a living cuckoo clock for the amusement of the Forsaken. And not that we have to endure being spat upon and mocked every waking minute of our time here... What pisses me off the most is being made to wear these damned short--shorts...', 12, 0, 100, 1, 0, 0, 'Text 2');
INSERT INTO `creature_text` VALUES (27198, 2, 0, 'I hope one of us dies. I hope that one is you, Skrotee.', 12, 0, 100, 1, 0, 0, 'Text 3');
INSERT INTO `creature_text` VALUES (27198, 3, 0, 'Of all the hair--brained, idiotic, BRAINLESS ideas... \"Yeah Brote, it\'ll be great... The Forsaken will rain gold coins down on our heads!\"', 12, 0, 100, 1, 0, 0, 'Text 4');
INSERT INTO `creature_text` VALUES (27198, 4, 0, 'This is all your fault, you know...', 12, 0, 100, 1, 0, 0, 'Text 5');
INSERT INTO `creature_text` VALUES (27198, 5, 0, 'Pray Scrotee... Find religion... Because if we ever get out of here, you will be wishing you were back in here!', 12, 0, 100, 1, 0, 0, 'Text 6');
INSERT INTO `creature_text` VALUES (27198, 6, 0, 'Has it really been an hour?', 12, 0, 100, 1, 0, 0, 'Text 7');
INSERT INTO `creature_text` VALUES (27202, 0, 0, '%s attempts to run away in fear!', 16, 0, 100, 0, 0, 0, 'combat Flee');
INSERT INTO `creature_text` VALUES (27209, 0, 0, 'Come to play?', 12, 0, 100, 0, 0, 0, 'combat Say');
INSERT INTO `creature_text` VALUES (27234, 0, 0, 'You are being misled! The Onslaught is all lies! The Scourge and the Forsaken are not our enemies! Wake up!', 14, 0, 100, 5, 0, 0, 'Blacksmith Goodman');
INSERT INTO `creature_text` VALUES (27235, 0, 0, 'Renounce the Scarlet Onslaught! Don\'t listen to the lies of the high general and the grand admiral any longer!', 14, 0, 100, 5, 0, 0, 'Lead Cannoneer Zierhut');
INSERT INTO `creature_text` VALUES (27236, 0, 0, 'Abbendis is nothing but a harlot and Grand Admiral Westwind is selling her cheap like he sold us out!', 14, 0, 100, 5, 0, 0, 'Stable Master Mercer');
INSERT INTO `creature_text` VALUES (27237, 0, 0, 'High general Abbendis personally told me that it was a mistake to come north and that we\'re doomed! I urge you all to lay down your weapons and leave before it\'s too late!', 14, 0, 100, 5, 0, 0, 'Commander Jordan');
