INSERT INTO `creature_text` VALUES (27577, 0, 0, '%s growls.', 16, 0, 100, 0, 0, 409, 'Mayor Godfrey');
INSERT INTO `creature_text` VALUES (27577, 1, 0, '%s hisses!', 16, 0, 100, 0, 0, 410, 'Mayor Godfrey');
INSERT INTO `creature_text` VALUES (27577, 2, 0, 'Tell you nothing, preacher.', 12, 0, 100, 1, 0, 0, 'Mayor Godfrey');
INSERT INTO `creature_text` VALUES (27577, 3, 0, 'What can you do to me that Kel\'Thuzad has not? That the Lich King will not?', 12, 0, 100, 1, 0, 0, 'Mayor Godfrey');
INSERT INTO `creature_text` VALUES (27577, 4, 0, 'The book is your salvation, yes... but nothing will you know! NOTHING I SAY! NOTHING! ', 12, 0, 100, 1, 0, 0, 'Mayor Godfrey');
INSERT INTO `creature_text` VALUES (27577, 5, 0, 'No more LIGHT! NO MORE! I BEG YOU!', 12, 0, 100, 0, 0, 0, 'Mayor Godfrey');
INSERT INTO `creature_text` VALUES (27577, 6, 0, 'Never felt hurt like this!', 12, 0, 100, 0, 0, 0, 'Mayor Godfrey');
INSERT INTO `creature_text` VALUES (27577, 7, 0, 'It tells of the coming apocalypse. How this world will burn and be reborn from death.', 12, 0, 100, 1, 0, 0, 'Mayor Godfrey');
INSERT INTO `creature_text` VALUES (27577, 8, 0, 'NOOOO!!! I tell you! I tell you everything!', 12, 0, 100, 39, 0, 0, 'Mayor Godfrey');
INSERT INTO `creature_text` VALUES (27577, 9, 0, 'Humans... Beneath the earth of Wintergarde Village you built a mausoleum! Why do you think Naxxramas attacked that spot? Thel\'zan hides deep in your own crypt and sends a thousand-thousand corpses at you! Perish you will...', 12, 0, 100, 1, 0, 0, 'Mayor Godfrey');
INSERT INTO `creature_text` VALUES (27577, 10, 0, 'How? Humans truly are stupid, yes? Thel\'zan the Duskbringer! Thel\'zan the Lich! He who is born of human flesh and bone, sacrificed all for power, protected by the Lich King!', 12, 0, 100, 1, 0, 0, 'Mayor Godfrey');
INSERT INTO `creature_text` VALUES (27577, 11, 0, 'You cannot stop Thel\'zan! He bears the dark gift, bestowed upon him by the Lich King himself!', 12, 0, 100, 1, 0, 0, 'Mayor Godfrey');
