INSERT INTO `creature_text` VALUES (27656, 0, 0, 'You brash interlopers are out of your element! I will ground you!', 14, 0, 0, 0, 0, 0, 'Boss Eregos - Aggro');
INSERT INTO `creature_text` VALUES (27656, 1, 0, 'Such insolence... such arrogance... must be PUNISHED!', 14, 0, 0, 0, 0, 0, 'Boss Eregos - Enrage');
INSERT INTO `creature_text` VALUES (27656, 2, 0, 'Savor this small victory, foolish little creatures. You and your dragon allies have won this battle. But we will win... the Nexus War.', 14, 0, 0, 0, 0, 0, 'Boss Eregos - Death');
