INSERT INTO `creature_text` VALUES (27683, 0, 0, 'I\'ve lived here and kept this shrine with the blessing of the Alexstrasza for longer than I can remember. In its darkest hour, I\'ve failed... I fell at the feet of that damned deathknight... Bloodbane.', 12, 0, 100, 1, 0, 0, 'Text 1');
INSERT INTO `creature_text` VALUES (27683, 1, 0, 'Dahlia spits on the ground at the thought of the name.', 16, 0, 100, 0, 0, 0, 'Text 2');
INSERT INTO `creature_text` VALUES (27683, 2, 0, 'The Lich King is stretching his hand far now... fearlessly treading on the most sacred lands of all the flights. He intends to force us to action', 12, 0, 100, 1, 0, 0, 'Text 3');
INSERT INTO `creature_text` VALUES (27683, 3, 0, 'My time is done... I offered myself to Alexstrasza in life, but I have nothing left to offer in death. She will need a new keeper for her shrine.', 12, 0, 100, 1, 0, 0, 'Text 4');
INSERT INTO `creature_text` VALUES (27683, 4, 0, 'And thank you... thank you for freeing me from the shackles of the damned. You are a hero... I know you will go on to great deeds', 12, 0, 100, 1, 0, 0, 'Text 5');
INSERT INTO `creature_text` VALUES (27683, 5, 0, 'The Remnant of Dahlia Suntouch fades away...', 16, 0, 100, 0, 0, 0, 'Text 6');
