INSERT INTO `creature_text` VALUES (27842, 0, 0, 'Bat gizzards again for the gnomes tonight...', 12, 0, 100, 1, 0, 0, 'Fenrick Barlowe text');
INSERT INTO `creature_text` VALUES (27842, 0, 1, 'What do they expect, making the bats come in at that angle? Broken necks and gamey bat stew, that\'s what they get.', 12, 0, 100, 1, 0, 0, 'Fenrick Barlowe text');
INSERT INTO `creature_text` VALUES (27842, 0, 2, '\'We like trees, Fenrick. They provide cover.\' They won\'t let me chop them down, either.', 12, 0, 100, 1, 0, 0, 'Fenrick Barlowe text');
INSERT INTO `creature_text` VALUES (27842, 0, 3, 'I wonder how many reinforcements need to suffer injury before they allows us to chop down these idiotic trees. They\'re costing us a fortune in bats. Maybe I\'ll rig a harness or two...', 12, 0, 100, 1, 0, 0, 'Fenrick Barlowe text');
INSERT INTO `creature_text` VALUES (27846, 0, 0, 'Patchy?', 12, 0, 100, 1, 0, 0, 'Text 1');
