INSERT INTO `creature_text` VALUES (27871, 0, 0, '%s goes into a frenzy!', 16, 0, 100, 0, 0, 0, 'combat Frenzy');
INSERT INTO `creature_text` VALUES (27915, 0, 0, 'Good work with the crates! Come talk to me in front of Stratholme for your next assignment!', 14, 0, 0, 0, 0, 0, 'Chromie - SAY_EVENT_START');
INSERT INTO `creature_text` VALUES (27959, 0, 0, 'You think that you can get rid of me through meditation?', 15, 0, 100, 1, 0, 0, 'Inner Turmoil text 0');
INSERT INTO `creature_text` VALUES (27959, 1, 0, 'Fool! I will destroy you and finally become that which has been building inside of you all of these years!', 15, 0, 100, 1, 0, 0, 'Inner Turmoil text 1');
INSERT INTO `creature_text` VALUES (27959, 2, 0, 'You cannot defeat me. I\'m an inseparable part of you!', 15, 0, 100, 1, 0, 0, 'Inner Turmoil text 50%');
INSERT INTO `creature_text` VALUES (27959, 3, 0, 'NOOOOOOOoooooooooo!', 15, 0, 100, 1, 0, 0, 'Inner Turmoil text death');
INSERT INTO `creature_text` VALUES (27990, 0, 0, 'You\'re too late, $n. Another visitor from Dalaran came asking after information about the same prismatic dragon blades.', 12, 0, 100, 1, 0, 0, 'Krasus');
INSERT INTO `creature_text` VALUES (27990, 1, 0, 'From your description, I\'m certain the book I loaned our visitor could allow you to easily identify the weapon.', 12, 0, 100, 25, 0, 0, 'Krasus');
INSERT INTO `creature_text` VALUES (27990, 2, 0, 'I\'m afraid you\'ll have to ask the -- Well, perhaps Kalecgos can help.', 12, 0, 100, 1, 0, 0, 'Krasus');
INSERT INTO `creature_text` VALUES (27990, 3, 0, '$n may have found the remains of a prismatic blade, Kalecgos. Will you offer your help to our visitor?', 12, 0, 100, 1, 0, 0, 'Krasus');
INSERT INTO `creature_text` VALUES (27990, 4, 0, 'You believe our allies will not be able to control the power of the swords?', 12, 0, 100, 1, 0, 0, 'Krasus');
INSERT INTO `creature_text` VALUES (27990, 5, 0, 'As will we all.', 12, 0, 100, 25, 0, 0, 'Krasus');
INSERT INTO `creature_text` VALUES (27990, 6, 0, 'Please, mortal, speak with Arcanist Tybalin in Dalaran. He may be able to negotiate with the Sunreavers for access to the book.', 12, 0, 100, 1, 0, 0, 'Krasus');
INSERT INTO `creature_text` VALUES (27990, 7, 0, 'Please, mortal, seek out Magister Hathorel in Dalaran. He might be able to negotiate with the Silver Covenant for access to the book.', 12, 0, 100, 1, 0, 0, 'Krasus');
