INSERT INTO `creature_text` VALUES (28095, 0, 1, 'Ahh! It\'s coming right for us!', 12, 0, 50, 0, 0, 0, 'combat Say');
INSERT INTO `creature_text` VALUES (28095, 1, 0, 'Dumb! Don\'t shoot at it! Gekgek want trophy in one piece!', 12, 0, 50, 0, 0, 0, 'combat Say');
