INSERT INTO `creature_text` VALUES (36626, 5, 0, '%s begins to cast |cFFFF7F00Pungent Blight!|r', 14, 0, 0, 0, 0, 0, 'Festergut - EMOTE_WARN_PUNGENT_BLIGHT');
INSERT INTO `creature_text` VALUES (36626, 6, 0, '%s vomits.', 12, 0, 0, 0, 0, 0, 'Festergut - EMOTE_PUNGENT_BLIGHT');
INSERT INTO `creature_text` VALUES (36626, 7, 0, 'Daddy, I did it!', 14, 0, 0, 0, 0, 16902, 'Festergut - SAY_KILL_1');
INSERT INTO `creature_text` VALUES (36626, 7, 1, 'Dead, dead, dead!', 14, 0, 0, 0, 0, 16903, 'Festergut - SAY_KILL_2');
INSERT INTO `creature_text` VALUES (36626, 8, 0, 'Fun time over!', 14, 0, 0, 0, 0, 16905, 'Festergut - SAY_BERSERK');
INSERT INTO `creature_text` VALUES (36626, 9, 0, 'Da ... Ddy...', 14, 0, 0, 0, 0, 16904, 'Festergut - SAY_DEATH');
