INSERT INTO `creature_text` VALUES (36627, 4, 0, '|TInterface\\Icons\\spell_shadow_unstableaffliction_2.blp:16|t%s begins to cast |cFFFF0000Unstable Ooze Explosion!|r', 41, 0, 0, 0, 0, 0, 'Rotface - EMOTE_UNSTABLE_EXPLOSION');
