INSERT INTO `creature_text` VALUES (37970, 4, 0, 'Dinner... is served.', 14, 0, 0, 0, 0, 16681, 'Prince Valanar - SAY_VALANAR_KILL_1');
INSERT INTO `creature_text` VALUES (37970, 4, 1, 'Do you see NOW the power of the Darkfallen?', 14, 0, 0, 0, 0, 16682, 'Prince Valanar - SAY_VALANAR_KILL_2');
