INSERT INTO `creature_text` VALUES (37970, 6, 0, '...why...?', 14, 0, 0, 0, 0, 16683, 'Prince Valanar - SAY_VALANAR_DEATH');
INSERT INTO `creature_text` VALUES (37972, 0, 0, 'Such wondrous power! The Darkfallen Orb has made me INVINCIBLE!', 14, 0, 0, 0, 0, 16727, 'Prince Keleseth - SAY_KELESETH_INVOCATION');
