INSERT INTO `creature_text` VALUES (37973, 0, 0, 'Tremble before Taldaram, mortals, for the power of the orb flows through me!', 14, 0, 0, 0, 0, 16857, 'Prince Taldaram - SAY_TALDARAM_INVOCATION');
