INSERT INTO `creature_text` VALUES (38004, 0, 0, 'Foolish mortals. You thought us defeated so easily? The San\'layn are the Lich King\'s immortal soldiers! Now you shall face their might combined!', 14, 0, 0, 1, 0, 16795, 'Blood-Queen Lana\'thel - SAY_INTRO_1');
INSERT INTO `creature_text` VALUES (38004, 1, 0, 'Rise up, brothers, and destroy our enemies.', 14, 0, 0, 0, 0, 16796, 'Blood-Queen Lana\'thel - SAY_INTRO_2');
INSERT INTO `creature_text` VALUES (38017, 0, 0, 'Are you certain you should be helping these mortals in their quest for the sword?', 12, 0, 100, 1, 0, 0, 'Kalecgos');
INSERT INTO `creature_text` VALUES (38017, 1, 0, 'These blades, Krasus... They were made long ago, when things were... different.', 12, 0, 100, 1, 0, 0, 'Kalecgos');
INSERT INTO `creature_text` VALUES (38017, 2, 0, 'Our enemies once turned our strongest weapon against us. What makes you think the prismatic blades will be any different?', 12, 0, 100, 1, 0, 0, 'Kalecgos');
INSERT INTO `creature_text` VALUES (38017, 3, 0, 'As you wish, my queen. I will not stand in their way, but I will keep a close watch.', 12, 0, 100, 16, 0, 0, 'Kalecgos');
