INSERT INTO `creature_text` VALUES (35911, 1, 0, 'We were fools to take up arms against each other, Darius.  The worgen would\'ve never stood a chance.', 12, 0, 100, 0, 0, 19720, 'King Genn Greymane');
INSERT INTO `creature_text` VALUES (35552, 0, 0, 'We need to keep the worgen\'s attention in the city, Genn.  It\'s the only shot we have for the survivors to make it to Duskhaven.', 12, 0, 100, 0, 0, 19507, 'Lord Darius Crowley');
INSERT INTO `creature_text` VALUES (35552, 1, 0, 'Not a chance, boy.  Gilneas is going to need its king\'s undivided attention.  Can\'t have your father wondering whether his child is alive or not.', 12, 0, 100, 0, 0, 19508, 'Lord Darius Crowley');
INSERT INTO `creature_text` VALUES (35552, 2, 0, 'My men and I will hole up inside the Light\'s Dawn Cathedral.  I\'ve already given the order and the cannons are on their way.  Lead our people well, Genn.', 12, 0, 100, 0, 0, 19509, 'Lord Darius Crowley');
INSERT INTO `creature_text` VALUES (35551, 0, 0, 'I\'ll stay behind with the Royal Guard, father.  It is my duty to Gilneas.', 12, 0, 100, 0, 0, 19622, 'Prince Liam Greymane');
INSERT INTO `creature_text` VALUES (35230, 1, 2, 'Come and get us, you motherless beasts!', 14, 0, 100, 0, 0, 19503, 'Lord Darius Crowley');
INSERT INTO `creature_text` VALUES (35230, 1, 1, 'Over here, you flea bags!', 14, 0, 100, 0, 0, 19504, 'Lord Darius Crowley');
INSERT INTO `creature_text` VALUES (35230, 1, 0, 'You\'ll never catch us, you blasted mongrels!', 14, 0, 100, 0, 0, 19505, 'Lord Darius Crowley');
INSERT INTO `creature_text` VALUES (35230, 0, 0, 'Let\'s round up as many of them as we can.  Every worgen chasing us is one less worgen chasing the survivors!', 12, 0, 100, 0, 0, 19506, 'Lord Darius Crowley');
INSERT INTO `creature_text` VALUES (32970, 0, 0, 'A new victim! Me was almost don wit dis one anyway.', 12, 0, 100, 0, 0, 0, 'combat Say');
